const initialData = {

    list : []
}




const todoReducers = (state, action) => {

    switch(action.type){
        case "ADD_TODO" : 

        const {id, data} = action.paload;

        return{
            ...state,
            data:[
                ...state.list,
                {
                id:id,
                data:data
            }]
        }

        default: return state;
    }




}
export default todoReducers
